    border: 'none',
    borderRadius: '10px',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s',
  },
  btnSecondary: {
    padding: '15px',
    background: 'transparent',
    color: '#007AFF',
    border: 'none',
    fontSize: '14px',
    cursor: 'pointer',
  },
  securityBadges: {
    marginTop: '30px',
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  badge: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    fontSize: '12px',
    color: '#666',
  },
  screenContainer: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  },
  screenTitle: {
    fontSize: '28px',
