    marginBottom: '15px',
  },
  quickLocationsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '8px',
  },
  quickLocationBtn: {
    background: '#f0f0f0',
    border: 'none',
    borderRadius: '10px',
    padding: '12px 8px',
    cursor: 'pointer',
    transition: 'all 0.3s',
    textAlign: 'center',
  },
  locationCard: {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    padding: '15px',
    background: '#f8f8f8',
    borderRadius: '12px',
    marginBottom: '12px',
    transition: 'all 0.3s',
  },
  infoCard: {
    background: '#E3F2FD',
    borderRadius: '12px',
    padding: '15px',
    marginTop: '20px',
    borderLeft: '4px solid #007AFF',
  },
  quickActions: {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '10px',
  },
  quickAction: {
    background: '#f0f0f0',
    border: 'none',
    borderRadius: '12px',
    padding: '15px 10px',
    cursor: 'pointer',
    transition: 'all 0.3s',
  },
  quickIcon: {
    fontSize: '36px',
    marginBottom: '5px',
  },
  quickLabel: {
    fontSize: '11px',
    fontWeight: '500',
  },
  successAlert: {
    background: '#34C759',
    color: 'white',
    padding: '15px',
    borderRadius: '12px',
    marginBottom: '20px',
    textAlign: 'center',
    fontWeight: '600',
  },
  checkInItem: {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    padding: '12px',
    background: '#f8f8f8',
    borderRadius: '12px',
    marginBottom: '10px',
  },
  checkInAvatar: {
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontWeight: 'bold',
  },
  checkInInfo: {
    flex: 1,
  },
  checkInName: {
